// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

contract EdinarStaking is Ownable, ReentrancyGuard {
    IERC20 public immutable edinarToken;
    
    uint256 public totalStaked;
    uint256 public rewardRate; // EDINAR per second per staked token
    uint256 public constant REWARD_PRECISION = 1e18;
    
    mapping(address => uint256) public stakedBalance;
    mapping(address => uint256) public rewards;
    mapping(address => uint256) public lastClaimTime;
    
    // Referral system
    mapping(address => address) public referrers;
    mapping(address => uint256) public referralCount;
    mapping(address => uint256) public referralRewards;
    uint256 public referralPercentage = 1; // Updated to 1%
    
    event Staked(address indexed user, uint256 amount, address indexed referrer);
    event Withdrawn(address indexed user, uint256 amount);
    event RewardClaimed(address indexed user, uint256 amount);
    event ReferralRewardPaid(address indexed referrer, address indexed referral, uint256 amount);
    event RewardRateUpdated(uint256 newRate);

    constructor(IERC20 _edinarToken, uint256 _initialRewardRate) {
        edinarToken = _edinarToken;
        rewardRate = _initialRewardRate;
    }

    function stake(uint256 amount, address referrer) external nonReentrant {
        require(amount > 0, "Amount must be > 0");
        
        _updateRewards(msg.sender);
        
        // Set referrer if not already set and valid
        if (referrer != address(0) && referrer != msg.sender && referrers[msg.sender] == address(0)) {
            referrers[msg.sender] = referrer;
            referralCount[referrer]++;
        }
        
        stakedBalance[msg.sender] += amount;
        totalStaked += amount;
        
        require(edinarToken.transferFrom(msg.sender, address(this), amount), "Transfer failed");
        emit Staked(msg.sender, amount, referrer);
    }

    function withdraw(uint256 amount) external nonReentrant {
        require(amount > 0, "Amount must be > 0");
        require(stakedBalance[msg.sender] >= amount, "Insufficient balance");
        
        _updateRewards(msg.sender);
        stakedBalance[msg.sender] -= amount;
        totalStaked -= amount;
        
        require(edinarToken.transfer(msg.sender, amount), "Transfer failed");
        emit Withdrawn(msg.sender, amount);
    }

    function claimRewards() external nonReentrant {
        _updateRewards(msg.sender);
        uint256 reward = rewards[msg.sender];
        require(reward > 0, "No rewards");
        
        rewards[msg.sender] = 0;
        
        // Pay referral rewards if applicable
        address referrer = referrers[msg.sender];
        if (referrer != address(0)) {
            uint256 referralAmount = (reward * referralPercentage) / 100;
            referralRewards[referrer] += referralAmount;
            require(edinarToken.transfer(referrer, referralAmount), "Referral transfer failed");
            emit ReferralRewardPaid(referrer, msg.sender, referralAmount);
            reward -= referralAmount;
        }
        
        require(edinarToken.transfer(msg.sender, reward), "Transfer failed");
        emit RewardClaimed(msg.sender, reward);
    }

    function _updateRewards(address user) internal {
        if (stakedBalance[user] == 0) {
            lastClaimTime[user] = block.timestamp;
            return;
        }
        
        uint256 timePassed = block.timestamp - lastClaimTime[user];
        uint256 newRewards = (stakedBalance[user] * timePassed * rewardRate) / REWARD_PRECISION;
        
        rewards[user] += newRewards;
        lastClaimTime[user] = block.timestamp;
    }

    function getPendingRewards(address user) external view returns (uint256) {
        if (stakedBalance[user] == 0) return 0;
        
        uint256 timePassed = block.timestamp - lastClaimTime[user];
        return rewards[user] + (stakedBalance[user] * timePassed * rewardRate) / REWARD_PRECISION;
    }

    function setRewardRate(uint256 newRate) external onlyOwner {
        require(newRate > 0, "Invalid rate");
        rewardRate = newRate;
        emit RewardRateUpdated(newRate);
    }
    
    function setReferralPercentage(uint256 newPercentage) external onlyOwner {
        require(newPercentage <= 20, "Max 20%");
        referralPercentage = newPercentage;
    }
}
